import logging
from unittest.mock import MagicMock, patch

from layer_values_monitor.telliot_feeds import get_source_from_data

from telliot_feeds.datasource import DataSource

mock_logger = MagicMock(spec=logging.Logger)


def test_get_source_from_data():
    spot_query_data = (
        # xsa/usd
        "",
        # ggg/usd
        "",
    )
    for query_data in spot_query_data:
        data_source = get_source_from_data(bytes.fromhex(query_data), mock_logger)
        assert isinstance(data_source, DataSource)


def test_non_existing_query_type():
    with patch("layer_values_monitor.logger.logger.error") as patched_error:
        mock_logger.error = patched_error
        bad_query = ""  # noqa: E501
        data_source = get_source_from_data(bytes.fromhex(bad_query), mock_logger)
        patched_error.assert_called_once_with("Unsupported query type: nonexisting")
        assert data_source is None


def test_query_types_exists_no_abi_attribute():
    with patch("layer_values_monitor.logger.logger.error") as patched_error:
        mock_logger.error = patched_error
        query_data = ""  # noqa: E501
        data_source = get_source_from_data(bytes.fromhex(query_data), mock_logger)

        patched_error.assert_called_once_with("query type RandomSource doesn't have abi attirbute to decode params")
        assert data_source is None


def test_query_types_exists_no_feed():
    with patch("layer_values_monitor.logger.logger.error") as patched_logger:
        mock_logger.error = patched_logger
        query_data = ""  # noqa: E501
        data_source = get_source_from_data(bytes.fromhex(query_data), mock_logger)

        patched_logger.assert_called_once_with("query type FileCIDQuery not supported by datafeed builder")
        assert data_source is None
